            setSearchMenu();
            runModuleTools();
            runDropdownEnduring();
            runTooltips();
            runESlider();
            runPopovers();
            runPanelScroll();
            runAnimatedElements();
            runShowTab();
            runCustomCheck();
            runColorPalette();
            runSaveSetting();
            runCustomSetting();
            runStatusButton();
            runCheckAll();
            runClearSetting();
            runClosedBarButton();
            runAnimateProgressbar();
